<template>
	<aside class="sidebar uk-light uk-padding-small">
		<a href="/" class="img uk-margin"></a>
		<ul class="uk-nav uk-nav-default uk-margin">
			<li v-for="nav in navs" v-if="nav.url"><router-link :to="nav.url"><i :class="nav.icon"></i> {{nav.name}}</router-link></li>
			<li class="uk-nav-header" v-else>{{nav.name}}</li>
			<li><a @click.prevent="logout()"><i class="icon-power"></i> Выйти из аккаунта</a></li>
		</ul>
	</aside>
</template>

<script>
	export default {
		data: function() {
			return { navs: [] }
		},
		methods: {
			getNavs: function() {
				this.$http.get('/ajax/menu.json').then(function(r) {
					this.navs = r.body;
				}, console.log);
			},
			logout: function () {
                this.$cookie.delete('token');
                this.$router.push({ path: 'login' });
            }
		},
		created: function() {
			this.getNavs();
		}
	}
</script>

